import re
import json

def extract_triples(text):
    # Step 1: Read the text carefully and identify the entities (subject, object) and the relations between them.
    entities = {}
    relations = {}
    for sentence in text.split('.'):
        sentence = sentence.strip()
        if sentence:
            # Extract entities
            entities_list = re.findall(r'\b[A-Za-z]+[^\.\,]*\b', sentence)
            for entity in entities_list:
                if entity not in entities:
                    entities[entity] = {'type': 'entity', 'id': len(entities)}
            # Extract relations
            relation_list = re.findall(r'\b[A-Za-z]+[^\.\,]*\b', sentence)
            for relation in relation_list:
                if relation not in relations:
                    relations[relation] = {'type':'relation', 'id': len(relations)}

    # Step 2: Extract the entities and relations from the text.
    triples = []
    for sentence in text.split('.'):
        sentence = sentence.strip()
        if sentence:
            # Extract subject and object
            subject = None
            object = None
            for entity in entities_list:
                if entity in sentence:
                    if subject is None:
                        subject = entity
                    else:
                        object = entity
            # Extract predicate
            for relation in relation_list:
                if relation in sentence:
                    predicate = relation
                    break
            # Create triple
            triple = {'subject': subject, 'predicate': predicate, 'object': object}
            triples.append(triple)

    # Step 3: Convert the extracted entities and relations into knowledge triples.
    knowledge_triples = []
    for triple in triples:
        subject_id = entities[triple['subject']]['id']
        object_id = entities[triple['object']]['id']
        predicate_id = relations[triple['predicate']]['id']
        knowledge_triple = {'subject': subject_id, 'predicate': predicate_id, 'object': object_id}
        knowledge_triples.append(knowledge_triple)

    # Step 4: Return the knowledge triples in JSON format.
    return json.dumps(knowledge_triples)
